/*
Grow and shrink the site logo at the 100px top breakpoint
Chris A. White | fyrebear.com
*/
function growShrinkLogo(){var o=document.getElementById("Logo");document.body.scrollTop>100||document.documentElement.scrollTop>100?o.style.width="40px":o.style.width="80px"}window.onscroll=function(){growShrinkLogo()};